          clearTimeout(initCrmapTimeout);
          jQuery(this).popover({
               trigger: 'manual',
               placement: 'bottom',
               container: 'body',
               html: true,
               delay: { hide: 500 },
               title: 'Rychl volba regionu',
               content: function(){
                    return '<div id="crmap"></div>';
               }
          });
          jQuery(this).popover('show');
          initSVGMap();
     });
     jQuery('.crmap').mouseleave(function(){ initCrmapTimeout = setTimeout('killmap()',500); });
     jQuery(document).click(function(){ jQuery('.crmap').popover('destroy'); });
}

function killmap(){
     jQuery('.crmap').popover('destroy');
}

