function initEvents(){
     // hover
     jQuery('#calendar td').hover(function(){
          if(jQuery(this).hasClass('day')) jQuery(this).addClass('hover');
     },function(){
          jQuery(this).removeClass('hover');
     });
     // poper
     jQuery('#calendar .haveevents').each(function(){
          var title = jQuery(this).attr('data-day');
          var manual = false;
          jQuery(this).popover({
               title: title,
               trigger: 'manual',
               placement: 'bottom',
               container: 'body',
               html: true,
               content: function(){
                    var list = '<ul class="popevents">';
                    jQuery('i',jQuery(this)).each(function(index,obj){
                         list += '<li>';
                              list += '<i class="event-icon event-type-'+jQuery(obj).attr('data-type')+'"></i><strong>'+jQuery(obj).attr('data-title')+'</strong><br>';
                              list += '<div class="info"><strong>as:</strong> '+jQuery(obj).attr('data-time')+', <strong>Msto:</strong> '+jQuery(obj).attr('data-place')+'</div>';
                              list += jQuery(obj).attr('data-description');
                         list += '</li>';
                    });
                    return list+'</ul>';
               }
          });
          jQuery(this).click(function(e){
               manual = !manual;
               e.stopPropagation();
          });
          jQuery(this).mouseenter(function(){
               if(!manual) jQuery(this).popover('show');
          });
          jQuery(this).mouseleave(function(){
               if(!manual) jQuery(this).popover('hide');
          });
          jQuery(document).click(function(){
               manual = false;
               jQuery('#calendar .haveevents').each(function(){ jQuery(this).popover('hide'); });
          });
     });

     // filtr
     //var fstatus = false;
     jQuery('a[href="#filter-events"]').click(function(){
          jQuery('#filter').slideToggle();
     });
}

function initTz(){
     jQuery('.carousel').carousel('cycle');
}

function initYoutube(){

     html5lightbox_options = {
          watermark: DOKU_TPL+'images/logom.png',
          skinsfoldername: DOKU_TPL+'images/'
     };

     initPirateVideos = function(json){
          for(var i=0;i<json.data.items.length;i++){
               var item = json.data.items[i];
               var minutes = Math.floor(parseInt(item.duration)/60);
               var seconds = parseInt(item.duration)-minutes*60;
               jQuery("#youtube ul").append('<li><div class="time">'+minutes+':'+seconds+'</div><a class="html5lightbox" href="'+item.player["default"]+'"><img src="'+(item.thumbnail.sqDefault).replace('http:','https:')+'" alt="'+item.title+'" title="'+item.description+'" /></a><br /><a href="'+item.player["default"]+'" title="'+item.description+'">'+item.title+'</a></li>');
          }
          
          jQuery('<script></script>').attr('type', 'text/javascript').attr('src', DOKU_TPL+'html5lightbox.js').appendTo(jQuery('head'));
     }

     jQuery('<script></script>').attr('type', 'text/javascript').attr('src', 'https://gdata.youtube.com/feeds/api/users/CeskaPiratskaStrana/uploads?v=2&alt=jsonc&prettyprint=true&max-results=3&callback=initPirateVideos').appendTo(jQuery('head'));

}



