	return new Array(x,y);
}
function tablecalcVal(x,y,table) {
	var v='notset';
	if ((x>=0) && (y>=0)) {
		if (typeof table.rows != 'undefined') {
			if (typeof table.rows[y] != 'undefined') {
				if (typeof table.rows[y].cells[x] != 'undefined') {
