        var options = '';
        jQuery('fieldset.options input').each(function(i, input){
            var $label = jQuery(this).parent();

            if(input.checked && ( !$label.hasClass('js')||jQuery('input#js').is(':checked') )){
                //add option
                options += ' '+input.id;

