     }
     if(jQuery('#title').val().length>255){
          piratitask_error(LANG.plugins.piratitask.title_long,false);
          stat = false;
     }
     // priority
     if(jQuery('#priority').val()<0 || jQuery('#priority').val()>3){
          piratitask_error(LANG.plugins.piratitask.priority_bad,false);
          stat = false;
     }
     // term
