               jQuery(dw_editor.init);
               //  init datepickers
               jQuery('.bootstrap-datetimepicker-widget').remove();
               jQuery('#datepicker-start,#datepicker-end,#datepicker-repeatend').datetimepicker({
                    language: 'cs',
                    weekStart: 1,
