                              dateend: jQuery('#form-newevent-dateend').val(),
                              interval: jQuery('#form-newevent-interval').val(),
                              freq: jQuery('#form-newevent-freq').val(),
                              dayofmonth: jQuery('#form-newevent-dayofmonth').val(),
                              repeatend: jQuery('#form-newevent-repeatend').val(),
                              description: jQuery('#form-newevent-short').val(),
                              content: jQuery('#wiki__text').val(),
