}

// funkcnost tabu
function piratievent_initTabs(){
     // events
     jQuery('#piratievent li[data-toggle="tab"]').on('shown',function(e){
          e.preventDefault();
          var a = jQuery('a',jQuery(this));
          
          a.tab('show');
          jQuery(this).addClass('active');
          if(a.attr('href')=='#tab1'){
               piratievent_loadNewEvent();
          }
          if(a.attr('href')=='#tab2'){
               piratievent_loadEvents();
          }
          jQuery(location).attr('hash','loc:'+a.attr('href').replace('#',''));

          e.stopPropagation();
     });

     // url
     var hash = jQuery(location).attr('hash');
     if(hash=='#loc:tab1') jQuery('#piratievent .nav-tabs a[href="#tab1"]').tab('show');
     else if(hash=='#loc:tab2') jQuery('#piratievent .nav-tabs a[href="#tab2"]').tab('show');
     else jQuery('#piratievent .nav-tabs li:eq(1) a').tab('show');

}



